import './App.css'
import Login from './pages/Login'
import { useSelector } from "react-redux"

function App() {

  const username = useSelector(state => state.user.name)
  const authenticated = useSelector(state => state.user.authenticated)

  return (
    <div className="App">
      { authenticated ? <h1 className="Text">Welcome, {username}! Your balls are hanging really low today. Keep it up! </h1> : <Login /> } 
    </div>
  );
}

export default App;
