import React from "react"
import './styles.js'
import useStyles from "./styles"
import { useDispatch, useSelector } from "react-redux"
import { TextField, Button } from '@material-ui/core'
import { useFormik } from "formik"
import { setCurrentUser } from "../../actions/user"

const Login = ({ routes = [] }) => {
  const classes = useStyles()
  const dispatch = useDispatch()

  const initialValues = useSelector(state => state.user["currentUser"])
  console.log("user: ", initialValues)

  const formik = useFormik({
    initialValues: {
      name: 'Austin',
      secret: 'test'
    },
    onSubmit: values => {
      console.log(values)
      dispatch(setCurrentUser(values.name, values.secret))
    }
  })

  return (
    <div className={classes.login}>
      <form className={classes.form} onSubmit={formik.handleSubmit}>
        <div className={classes.textContainer}>
          <TextField 
            label="Name" 
            onChange={formik.handleChange}
            value={formik.values.name}
            className={classes.textField}
            classes={{
              root: classes.textRoot
            }}/>
          <TextField 
            label="Secret" 
            onChange={formik.handleChange}
            value={formik.values.secret}
            className={classes.textField}
            classes={{
              root: classes.textRoot
            }}/>
        </div>
        <Button variant="contained" type="submit" className={classes.submitButton}>Enter</Button>
      </form>
    </div>
  )
}

export default Login
