import { USER_SET_USER } from "../types/actions/user"

const userState = {
  currentUser: {
    name: ""
  }
}
const User = (state = userState, action) => {
  switch (action.type) {
    case USER_SET_USER:
        return {
          ...state,
          currentUser: action.data
        }
    default:
      return state
  }
}

export default User
