import {
  USER_SET_USER,
  USER_SET_AUTHENTICATED,
  USER_SET_LOADING,
  USER_SET_ERROR,
} from "../types/actions/user"

export const setCurrentUser = ( user, password ) => {
  console.log(user)
  console.log(password)
  if(password === "test"){
    console.log("correct")
    return dispatch => {
      dispatch(setUser(user))
    }
  }
  console.log("Wrong password")
}

export const setUser = (data) => ({
  type: USER_SET_USER,
  data
})

export const setAuth = (data) => ({
  type: USER_SET_AUTH,
  data
})

export const setLoading = (loading = true) => ({
  type: USER_SET_LOADING,
  loading
})

export const setError = (error = false) => ({
  type: USER_SET_ERROR,
  error
})
